Atotal &lt;- rbind(AMauer, AGonzalez, AMcCutchen, ACabrera, APuig, ADonaldson, AJudge, AHarper, ABaez, ATatis, AOhtani, AChisholm )
WARAVG &lt;- Atotal |&gt; 
  summarise(
    avgwar &lt;- mean(WAR)
  )
